s = "";
let start=0 , end = s.length, rev_s="", anS=s[0];
while(start<end){
    if(start==s.length-1){
        break;
    }
    if ((start+1)==end){
        start++;
        end= s.length;
    }
    else{
        rev_s = s.substring(start,end);
        rev_s = rev_s.split('').reverse().join('');
        if(s.substring(start,end)==rev_s){
            if(anS.length<rev_s.length){
                anS = rev_s;
            }
        }
            end--;
    }
}
console.log(anS)